import React, { useState } from 'react';
import './LoginBox.css'; // Estilos específicos para o LoginBox
import { useNavigate } from 'react-router-dom';

const LoginBox = () => {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const navigate = useNavigate();

    const handleLogin = () => {
        // Aqui você pode adicionar a lógica de validação de login, se necessário
        navigate('/catalog'); // Navega para a página de catálogo após o login
    };

    return (
        <div className="login-box">
            <h2>Login</h2>
            <input
                type="text"
                placeholder="Nome"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <input
                type="number"
                placeholder="Idade"
                value={age}
                onChange={(e) => setAge(e.target.value)}
            />
            <button onClick={handleLogin}>Ver Animes</button>
        </div>
    );
};

export default LoginBox;
