import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css'; // Certifique-se de que o CSS do App também está configurado
import LoginBox from './components/LoginBox';
import AnimeCatalog from './pages/AnimeCatalog';
import backgroundImage from './assets/background.gif'; // Importa o GIF de fundo

const App = () => {
    return (
        <Router>
            <div
                className="app"
                style={{
                    backgroundImage: `url(${backgroundImage})`,
                    height: '100vh',
                    backgroundSize: 'cover',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                <Routes>
                    <Route path="/" element={<LoginBox />} />
                    <Route path="/catalogo" element={<AnimeCatalog />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
