import React from 'react';
import './AnimeCatalog.css'; // Certifique-se de criar este arquivo CSS para os estilos

const animeList = [
    {
        title: 'Naruto',
        episodes: 220,
        synopsis: 'A saga de um ninja que busca se tornar o Hokage.',
        image: 'https://example.com/naruto.jpg', // Substitua por uma URL de imagem real
    },
    {
        title: 'One Piece',
        episodes: 1000,
        synopsis: 'A busca de um grupo de piratas pelo tesouro One Piece.',
        image: 'https://example.com/onepiece.jpg', // Substitua por uma URL de imagem real
    },
    // Adicione mais animes aqui
];

const AnimeCatalog = () => {
    return (
        <div className="anime-catalog">
            <h1>Catálogo de Animes</h1>
            <div className="anime-cards">
                {animeList.map((anime, index) => (
                    <div className="anime-card" key={index}>
                        <img src={anime.image} alt={anime.title} />
                        <h2>{anime.title}</h2>
                        <p>Número de episódios: {anime.episodes}</p>
                        <p>Sinopse: {anime.synopsis}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AnimeCatalog;
